export default {
  data: function data() {
    return {
      topQueryDialog: false,
      cancelAfterBuyDialog: false,
      applyAfterBuyDialog: false,
      addLogistDialog: false,
      cancelAfterBuyReason: '',
      jdGoods: 'primary',
      inputGoods: '',
      activeNames: ['1'],
      postForm: {
        name: '',
        goodsType1: '',
        goodsType2: '',
        goodsType3: '',
        timeState: ''
      },
      purchaseList: [{}],
      topQueryForm: {
        startPrice: '',
        endPrice: '',
        payTimeStart: '',
        payTimeEnd: '',
        followTimeStart: '',
        followTimeEnd: ''
      },
      applyDialigForm: {}
    };
  },
  methods: {
    topQuery: function topQuery() {
      //  高级检索
      this.topQueryDialog = true;
    },
    listDetail: function listDetail() {
      //  点击列表详情
      this.$router.push({
        name: 'listDetail'
      });
    },
    cancelAfterBuy: function cancelAfterBuy() {
      //  取消售后
      // todo...  
      //     校验接口
      this.cancelAfterBuyDialog = true;
    },
    handleList: function handleList() {
      var _this = this;

      //  处理
      this.$confirm('确认进行处理吗？', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this.$message({
          type: 'success',
          message: '处理成功!'
        });
      }).catch(function () {
        _this.$message({
          type: 'info',
          message: '已取消'
        });
      });
    },
    applyAfterBuy: function applyAfterBuy() {
      //  申请售后
      // todo...
      this.applyAfterBuyDialog = true;
    },
    addLogist: function addLogist() {
      //  补充物流
      this.addLogistDialog = true;
    },
    checkState: function checkState(state) {
      if (state == 'jd') {
        this.jdGoods = 'primary';
        this.inputGoods = '';
      } else {
        this.jdGoods = '';
        this.inputGoods = 'primary';
      }
    }
  }
};
