            submitTotal:'123',
            cancelAccountDialog:false,
            showReturnOrder:true,
            detailDialog:false,
            failReasonDialog:false,
        }
    },
    methods:{
        returnOrder() { // 退款

        },
        returnDetail() { // 详情
            this.detailDialog = true
        },
        failReason() { // 失败原因
            this.failReasonDialog = true
        },
        checkReturnOrder() { // 批量自动退款
            this.$confirm(`仅支持批量处理相同支付通道的任务`, {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(() => {
                this.showReturnOrder = false
            }).catch(() => {
                this.showReturnOrder = true     
            });
        },
        returnOrder(){ // 确认退款
            this.$confirm(`合计总金额xxx元，是否确认提交退款？`,'确认提交', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(()=>{
                // todo...
                this.$message({
                    type: 'success',
                    message: '成功!'
                })
                this.showReturnOrder = true
            }).catch(()=>{
                this.$message({
                    type: 'info',
                    message: '已取消'
                })  
                this.showReturnOrder = true
            })
        }

    }
}
